import React from 'react'
import { Container, Input } from './styles'



const SearchBar = ({setTerm}) => {

  const handleSearch = (e,city) =>{
    if(e.key === 13){
      setTerm(city)
    }
  }

  return (
   <Container>
     <div>
       <Input placeholder='Search City' onKeyPress={e=>{if(e.key === 13 ) setTerm(e.target.value)}}/>
     </div>
     
   </Container>
  )
}

export default SearchBar
