import styled from "styled-components";

export const Date = styled.h2`
    color:#ffffff ;
`

export const City = styled.h2`
    margin-top: 180px;
`
export const Container = styled.div`
    display:flex ;
    flex-direction:row ;
    justify-content: space-around;
    

    #weather{
        color:#ffffff ;
    }

    #temperature{
        color:#ffffff ;
    }

    #humidity{
        color:#ffffff ;
    }
`
