import React,{useEffect,useState} from 'react'
import styled from 'styled-components'
import SearchBar from './components/search/SearchBar'
import Weather from './components/main/Weather'
import { BsFillArrowLeftCircleFill as Left } from "react-icons/bs"
import { BsFillArrowRightCircleFill as Right } from "react-icons/bs"
import useGeoLocation from './hooks/useGeolocation'
import {getByLocation} from './api/index'

const Container = styled.div`
  background: #304352;  
  background: -webkit-linear-gradient(to right, #d7d2cc, #304352);  
  background: linear-gradient(to right, #d7d2cc, #304352);
  width: 800px;
  height: 475px; 
  display: flex;
  margin: auto;
  border-radius: 30px;

  #search-bar{
    margin-left: 255px;
  }

`
const Wrapper = styled.div`
  display:flex ;
  flex-direction:row ;

  #center{
    margin-left: 18px;
    margin-top: 75px;
    width: 725px;
    height: 330px;
    background: rgba( 203, 200, 200, 0.15 );
    box-shadow: 0 8px 32px 0 rgba( 31, 38, 135, 0.37 );
    backdrop-filter: blur( 0px );
    -webkit-backdrop-filter: blur( 0px );
    border-radius: 10px;
    border: 1px solid rgba( 255, 255, 255, 0.18 );
  }

  #left{
    margin-top: 220px;
  }
  #right{
    margin-top: 220px;
    margin-left: 20px;
  }
`


const App = () => {
  const location = useGeoLocation();
  const [cords, setCords] = useState({lat:location.coordinates.lat,lng:location.coordinates.lng})

  const fetchData = async () =>{
    try {
      const {data} = await getByLocation(cords.lat,cords.lng)
      console.log(data)
    } catch (error) {
      console.log(error.message)
    }

  }

  useEffect(() =>{  

  },)



  return (   
    <Container>     
      <div>
        <div id='search-bar'>
          <SearchBar />             
        </div>     
        <Wrapper>
          <div id='left'><Left onClick={fetchData} /></div>
          <div id='center'><Weather /></div>
          <div id='right'><Right /></div>
        </Wrapper>
      </div>
    </Container>
  )
}

export default App
